mapper.select:
SELECT * from ( SELECT rownum rnum, gl.* FROM ( SELECT * FROM (SELECT g.idx g_idx, g.title ,g.content , g.image ,g.max , g.open_date , g.secret ,g.password, c.category group_category, c.image group_image FROM CommunityGroup g, CommunityCategory c WHERE g.category_idx = c.idx order by g.idx desc) gk left join ( select count(*) as like_num, group_idx as l_group_idx from grouplike GROUP BY group_idx ) l on l.l_group_idx=gk.g_idx
where title LIKE '%' || #{searchContent} || '%'
where group_category LIKE '%' || #{searchContent} || '%'
where group_keyword LIKE '%' || #{searchContent} || '%'
) gl ) rgl where rgl.rnum between #{paging.startNo } and #{paging.endNo }
