mapper.select:
SELECT COUNT(*)
FROM ZakSimMember
WHERE id=#{id} AND password=#{password}

SELECT * FROM ( SELECT rownum rnum, Z.* FROM ( SELECT * FROM ZakSimMember WHERE id != 'admin' AND id LIKE '%' || #{searchId} || '%'
AND suspension_date IS NOT NULL
ORDER BY idx DESC ) Z ORDER BY rnum ) WHERE rnum BETWEEN #{paging.startNo} AND #{paging.endNo}

SELECT COUNT(*) FROM ZakSimMember
WHERE member_type = 'zaksim'
AND id = #{id}
AND password = #{password}

SELECT * FROM ZakSimMember
WHERE member_type = 'zaksim'
AND id = #{id}

SELECT id FROM ZakSimMember
WHERE member_type = 'zaksim'
AND name = #{name}
AND email = #{email}

SELECT COUNT(*) FROM ZakSimMember
WHERE member_type = 'zaksim'
AND id = #{id}
AND name = #{name}
AND email = #{email}

mapper.update:
UPDATE ZakSimMember
SET password = #{ranPw}
WHERE member_type = 'zaksim' AND id = #{id}
