mapper.select:
SELECT COUNT(*)
FROM ZakSimMember
WHERE id=#{id} AND password=#{password}

SELECT * FROM ( SELECT rownum rnum, Z.* FROM ( SELECT * FROM ZakSimMember WHERE id != 'admin' AND id LIKE '%' || #{searchId} || '%'
AND suspension_date IS NOT NULL
ORDER BY idx DESC ) Z ORDER BY rnum ) WHERE rnum BETWEEN #{paging.startNo} AND #{paging.endNo}
